void gauss(mat &v){
  Int n=v.size();
  for(Int i=0;i<n;i++){
    for(Int k=i;k<n;k++){
      if(v[k][i]){
        swap(v[i],v[k]);
        break;
      }
    }
    for(Int k=0;k<n;k++)
      if(i!=k&&v[k][i]) v[k]^=v[i];
  }
}

Int mrank(mat v,Int m){
  Int n=v.size();
  Int r=0,c=0;
  for(Int i=0;i<n;i++){
    Int s=-1;
    while(c<m){
      for(Int j=i;j<n;j++){
        if(v[j][c]){
          s=j;
          break;
        }
      }
      if(~s) break;
      c++;
    }
    if(c>=m) break;

    swap(v[i],v[s]);
    for(Int j=0;j<n;j++)
      if(i!=j&&v[j][c]) v[j]^=v[i];

    r++;c++;
  }
  return r;
}

