  rep (j, w) {
    if (a[j][j] == 0) {
      for (int i = j + 1; i < h; i++) {
        if (a[i][j] == 1) {
          swap(a[i], a[j]);
          break;
        }
      }
    }
    if (a[j][j] == 0) {
      no++;
      continue;
    }
    for (int i = 0; i < h; i++) {
      if (i == j) continue;
      a[i][j] = 0;
    }
  }
  ll ans = 0;
  for (int x = 1; x <= w - no; x += 2) {
    for (int y = 0; y <= w - no - x; y++) {
      ans += mod_pow(2, h - w) * nCr(w - no, x) % MOD * mod_pow(2, 2 * no) % MOD * nCr(w - no - x, y) % MOD * mod_pow(2, w - no - x - y) % MOD;
      ans %= MOD;
    }


  }
  cout << ans << endl;
}

