void Gauss(vector<bitset<300>> &B){
	int now = 0;
	rep(i,300){
		int temp = -1;
		for(int j=now;j<B.size();j++){
			if(B[j][i]){
				temp = j;
				break;
			}
		}
		if(temp==-1)continue;

		rep(j,B.size()){
			if(temp==j)continue;
			if(B[j][i])B[j] ^= B[temp];
		}

		swap(B[now],B[temp]);
		now++;
	}
}

int main(){

	int N,M;
	cin>>N>>M;

	vector<bitset<300>> B(N);

	rep(i,N){
		rep(j,M){
			int t;
			cin>>t;
			if(t==1){
				B[i][j] = 1;
			}
		}
	}

	Gauss(B);

	mint ans = mint(2).pow(M-1);

	mint diff = 1;
	rep(i,N){
		if(B[i]==0){
			diff *= 2;
		}
	}

	ans *= mint(2).pow(N) - diff;

	cout<<ans.val()<<endl;

	return 0;
}

