    rep(i, n) {
        rep(j, m) {
            int x; cin >> x;
            a[i][j] = x;
        }
    }
    int cz = count_zero(a);
    int ans = rui(2, n,p) - rui(2, cz,p); per(ans);
    ans*=rui(2, m - 1, p); per(ans);
    cout << ans << endl;
    return 0;
}

