using namespace std;

size_t rankF2(vector<vector<long>>& a){ // calculate rank of matrix in F_2
    long n = a.size(), m = a[0].size();
    size_t now(0), ret(0);
    while(now < m && ret < n){
        size_t i(0);
        for(; i < n; ++i)if(a[i][now])break;
        if(i == n){
            ++now;
            continue;
        }
        size_t j(i + 1);
        for(; j < n; ++j)if(a[j][now])for(int k = 0; k < m; ++k)a[j][k] ^= a[i][k];
        ++ret;
        ++now;
    }
    return ret;
}

int main(){
    constexpr long m = 998244353;
    auto modpow = [&m](long n){
        long r = 1, p = 2;
        while(n){
            if(n & 1){
                r *= p;
                r %= m;
            }
            p *= p;
            p %= m;
            n >>= 1;
        }
        return r;
    };

    long N, M;
    cin >> N >> M;
    bool f = false;
    if(N > M && (f = true))swap(N, M);

    vector<vector<long>> a(N, vector<long>(M));
    if(f)for(int i = 0; i < M; ++i)for(int j = 0; j < N; ++j)scanf("%ld", &a[j][i]);
    else for(auto& i : a)for(auto &j : i)scanf("%ld", &j);

    auto r = rankF2(a);
    cout << r << endl;
    cout << modpow(N + M - r - 1) * (modpow(r) - 1) % m << endl;
}

