template <typename T> int gaussianElimination(vector<vector<T>> &A, int var = -1) {
    int N = A.size(), M = A[0].size();
    if (var < 0) {
        var = N;
        assert(N <= M);
    }
    int k = 0, rank = var;
    rep(i, 0, var) {
        int v = -1;
        for (int j = k; j < N && v == -1; j++) if (A[j][i] != 0) v = j;
        if (v == -1) {
            rank--;
            continue;
        }
        //a[i] = k;
        swap(A[k], A[v]);
        T x = T(1) / A[k][i];
        rep(j, 0, M) A[k][j] = A[k][j] * x;
        rep(j, 0, N) if (j != k) {
            T y = A[j][i];
            for (int h = M - 1; h >= i; h--)
                A[j][h] = A[j][h] - A[k][h] * y;
        }
        k++;
    }

    return rank;
}
