        for (int j = m - 1, t; ~j; j--) {
            scanf("%d", &t);
            if (t) a[i].set(j);
        }
    }
    for (int i = 0; i < n; i++) {
        for (auto b : base) {
            auto c = b ^ a[i];
            for (int j = m - 1; ~j; j--) {
                if (c[j] < a[i][j]) { a[i] = c; break; }
                else if (c[j] > a[i][j]) break;
            }
        }
        if (a[i].any()) base.push_back(a[i]);
    }
    printf("%d\n", (qp(2, n + m - 1) - qp(2, n + m - 1 - base.size()) + P) % P);
    return 0;
}

