int Gauss(vector<vector<int>> &mat) {

    int rank = 0;
    int n = (int)mat.size();
    int m = (int)mat[0].size();

    for(int i = 0; i < m; i++) {
        bool ok = false;
        for(int j = i; j < n; j++) {
            if(mat[j][i]) {
                swap(mat[j], mat[i]);
                ok = true;
                break;
            }
        }

        if(!ok) continue;

        rank++;

        for(int j = i+1; j < n; j++) if(mat[j][i]) {

            for(int k = i; k < m; k++) {

                mat[j][k] ^= mat[i][k];
            }
        }
    }

    return rank;
}



