ll fast_pow(ll base, int n){
    ll carry = base;
    ll res = 1LL;

    while(n){
        if(n & 1){
            res = (res * carry) % mod;
        }

        n /= 2;
        carry = (carry * carry) % mod;
    }

    return res;
}

ll MOD(ll x){
    return ((x % mod) + mod) % mod;
}

