void swap_row (int l, int r) {
	for (int i = 0; i < m; i++)
		swap(Arr[l][i], Arr[r][i]);
}

void xor_row (int l, int r) {
	for (int i = 0; i < m; i++)
		Arr[r][i] ^= Arr[l][i];
}

int _sub (int a, int b) {
	int c = a - b;
	if (c < 0)
		c += MOD;
	return c;
}

int _mul (int a, int b) {
	int c = 1LL * a * b % MOD;
	return c;
}

int _pow (int a, int b) {
	int res = 1;
	for (; b; b >>= 1) {
		if (b & 1)
			res = _mul(res, a);
		a = _mul(a, a);
	}
	return res;
}

