		for (int j = 0; j < m; j++)
			cin >> Arr[i][j];

	for (int i = 0; i < m; i++) {
		int ind = -1;
		for (int j = i; j < n; j++)
			if (Arr[j][i])
				ind = j;

		if (ind == -1)
			continue;

		swap_row(i, ind);
		for (int j = i + 1; j < n; j++)
			if (Arr[j][i])
				xor_row(i, j);
	}

	int num = 0;
	for (int i = 0; i < n; i++)
		for (int j = 0; j < m; j++)
			if (Arr[i][j]) {
				num++;
				break;
			}

