int rref(int n, int m) {
	int sr = 0, pivots = 0;
	for (int j = 0; j < m; ++j) {
		for (int i = sr; i < n; ++i) {
			if (a[i][j]) {
				swap(a[i], a[sr]);
				break;
			}
		}
		if (!a[sr][j]) {
			continue;
		}
		++pivots;
		for (int i = sr + 1; i < n; ++i) {
			if (a[i][j]) {
				a[i] ^= a[sr];
			}
		}
		++sr;
	}
	return m - pivots;
}

