      q = d - c * q;
    }
    invf[n] = (int) (p < 0 ? p + mod : p);
    for (int i = n - 1; i >= 0; i--) {
      invf[i] = (int) ((long) invf[i + 1] * (i + 1) % mod);
    }
    return new int[][] {f, invf};
  }

  public static int rank(int[][] M, int p) {
    if (M.length == 0)
      return 0;
    int n = M.length, m = M[0].length;

    // Forward Elimination
    for (int i = 0; i < n; i++) {
      // select pivot
      boolean pivotFound = false;
      out: for (int pi = i; pi < n; pi++) {
        for (int pj = i; pj < m; pj++) {
          if (M[pi][pj] != 0) {
            // pivot found
            if (pj != i) {
              // swap columns
              for (int k = 0; k < n; k++) {
                int d = M[k][pj];
                M[k][pj] = M[k][i];
                M[k][i] = d;
              }
            }
            if (pi != i) {
              // swap rows
              int[] d = M[pi];
              M[pi] = M[i];
              M[i] = d;
            }
            pivotFound = true;
            break out;
          }
        }
      }
      if (!pivotFound)
        return i;

      long ID = invl(M[i][i], p);
      M[i][i] = 1;
      for (int j = i + 1; j < m; j++) {
        M[i][j] = (int) (M[i][j] * ID % p);
      }

      for (int j = i + 1; j < n; j++) {
        long B = p - M[j][i];
        M[j][i] = 0;
        for (int k = i + 1; k < m; k++) {
          M[j][k] = (int) ((M[j][k] + M[i][k] * B) % p);
        }
      }
    }
    return n;
  }

  public static long invl(long a, long mod) {
    long b = mod;
    long p = 1, q = 0;
    while (b > 0) {
      long c = a / b;
      long d;
      d = a;
      a = b;
      b = d % b;
      d = p;
      p = q;
      q = d - c * q;
    }
    return p < 0 ? p + mod : p;
  }

