import java.io.IOException;
import java.util.BitSet;
import java.io.BufferedReader;
import java.io.Reader;
import java.util.Comparator;
import java.io.InputStreamReader;
import java.io.InputStream;

/**
 * Built using CHelper plug-in
 * Actual solution is at the top
 */
public class Main {
    public static void main(String[] args) {
        InputStream inputStream = System.in;
        OutputStream outputStream = System.out;
        MyInput in = new MyInput(inputStream);
        PrintWriter out = new PrintWriter(outputStream);
        EOddSubrectangles solver = new EOddSubrectangles();
        solver.solve(1, in, out);
        out.close();
    }

    static class EOddSubrectangles {
        static final int mod = 998244353;

        public void solve(int testNumber, MyInput in, PrintWriter out) {
            int n = in.nextInt();
            int m = in.nextInt();
            BitSet[] bitset = new BitSet[n];
            for (int y = 0; y < n; y++) {
                bitset[y] = new BitSet();
                for (int x = 0; x < m; x++) {
                    if (in.nextInt() == 1) {
                        bitset[y].set(x);
                    }
                }
            }
            Arrays.sort(bitset, Comparator.comparing(BitSet::length).reversed());
            int rank = 0;
            for (int y = 0, x = 0; y < n && x < m; x++) {
                for (int i = y; i < n; i++) {
                    if (bitset[i].get(x)) {
                        swap(bitset, i, y);
                        break;
                    }
                }
                if (bitset[y].get(x)) {
                    for (int i = y + 1; i < n; i++) {
                        bitset[i].xor(bitset[y]);
                    }
                    rank++;
                    y++;
                }
            }

            out.println(((pow(2, n) - pow(2, n - rank)) * pow(2, m - 1) % mod + mod) % mod);
        }

        long pow(int n, int r) {
            long ans = 1;
            for (int i = 0; i < r; i++) ans = ans * n % mod;
            return ans;
        }

        static <T> void swap(T[] xs, int i, int j) {
            T t = xs[i];
            xs[i] = xs[j];
            xs[j] = t;
        }

    }

    static class MyInput {
        private final BufferedReader in;
        private static int pos;
        private static int readLen;
        private static final char[] buffer = new char[1024 * 8];
        private static char[] str = new char[500 * 8 * 2];
        private static boolean[] isDigit = new boolean[256];
        private static boolean[] isSpace = new boolean[256];
        private static boolean[] isLineSep = new boolean[256];

        static {
            for (int i = 0; i < 10; i++) {
                isDigit['0' + i] = true;
            }
            isDigit['-'] = true;
            isSpace[' '] = isSpace['\r'] = isSpace['\n'] = isSpace['\t'] = true;
            isLineSep['\r'] = isLineSep['\n'] = true;
        }

        public MyInput(InputStream is) {
            in = new BufferedReader(new InputStreamReader(is));
        }

        public int read() {
            if (pos >= readLen) {
                pos = 0;
                try {
                    readLen = in.read(buffer);
                } catch (IOException e) {
                    throw new RuntimeException();
                }
                if (readLen <= 0) {
                    throw new MyInput.EndOfFileRuntimeException();
                }
            }
            return buffer[pos++];
        }

        public int nextInt() {
            int len = 0;
            str[len++] = nextChar();
            len = reads(len, isSpace);
            int i = 0;
            int ret = 0;
            if (str[0] == '-') {
                i = 1;
            }
            for (; i < len; i++) ret = ret * 10 + str[i] - '0';
            if (str[0] == '-') {
                ret = -ret;
            }
            return ret;
        }

        public char nextChar() {
            while (true) {
                final int c = read();
                if (!isSpace[c]) {
                    return (char) c;
                }
            }
        }

        int reads(int len, boolean[] accept) {
            try {
                while (true) {
                    final int c = read();
                    if (accept[c]) {
                        break;
                    }
                    if (str.length == len) {
                        char[] rep = new char[str.length * 3 / 2];
                        System.arraycopy(str, 0, rep, 0, str.length);
                        str = rep;
                    }
                    str[len++] = (char) c;
                }
            } catch (MyInput.EndOfFileRuntimeException e) {
            }
            return len;
        }

        static class EndOfFileRuntimeException extends RuntimeException {
        }

    }
}

import java.io.ByteArrayInputStream;
import java.io.IOException;
import java.io.InputStream;
import java.io.PrintWriter;
import java.util.Arrays;
import java.util.InputMismatchException;

public class Main {
	static InputStream is;
	static PrintWriter out;
	static String INPUT = "";

	static void solve()
	{
		int n = ni(), m = ni();
		int[][] a = new int[n][];
		for(int i= 0;i < n;i++){
			a[i] = na(m);
		}
		boolean[][] b = new boolean[n][m];
		for(int i = 0;i < n;i++){
			for(int j= 0;j < m;j++){
				b[i][j] = a[i][j] == 1;
			}
		}
		int mod = 998244353;
		int r = rank(b);
		long ret = pow(2, m-1, mod) * (pow(2, n, mod) -pow(2, n-r, mod));
		ret %= mod;
		if(ret < 0)ret += mod;
		out.println(ret);
	}

	public static long pow(long a, long n, long mod) {
		//		a %= mod;
		long ret = 1;
		int x = 63 - Long.numberOfLeadingZeros(n);
		for (; x >= 0; x--) {
			ret = ret * ret % mod;
			if (n << 63 - x < 0)
				ret = ret * a % mod;
		}
		return ret;
	}


	public static int rank(boolean[][] M)
	{
		int n = M.length, m = M[0].length;

		// Forward Elimination
		for(int i = 0;i < n;i++){
			// select pivot
			boolean pivotFound = false;
			out:
			for(int pi = i;pi < n;pi++){
				for(int pj = i;pj < m;pj++){
					if(M[pi][pj]){
						// pivot found
						// swap columns
						for(int k = 0;k < n;k++){
							boolean u = M[k][pj]; M[k][pj] = M[k][i]; M[k][i] = u;
						}
						if(pi != i){
							// swap rows
							for(int k = 0;k < m;k++){
								boolean u = M[pi][k]; M[pi][k] = M[i][k]; M[i][k] = u;
							}
						}
						pivotFound = true;
						break out;
					}
				}
			}
			if(!pivotFound)return i;

			for(int j = i+1;j < n;j++){
				if(M[j][i]){
					for(int k = i;k < m;k++){
						M[j][k] ^= M[i][k];
					}
				}
			}
		}
		for(int j = n-1;j >= 0;j--){
			for(int i = j-1;i >= 0;i--){
				if(M[i][j]){
					for(int k = j;k < m;k++){
						M[i][k] ^= M[j][k];
					}
				}
			}
		}

		return n;
	}


	public static void main(String[] args) throws Exception
	{
		long S = System.currentTimeMillis();
		is = INPUT.isEmpty() ? System.in : new ByteArrayInputStream(INPUT.getBytes());
		out = new PrintWriter(System.out);

		solve();
		out.flush();
		long G = System.currentTimeMillis();
		tr(G-S+"ms");
	}

	private static boolean eof()
	{
		if(lenbuf == -1)return true;
		int lptr = ptrbuf;
		while(lptr < lenbuf)if(!isSpaceChar(inbuf[lptr++]))return false;

		try {
			is.mark(1000);
			while(true){
				int b = is.read();
				if(b == -1){
					is.reset();
					return true;
				}else if(!isSpaceChar(b)){
					is.reset();
					return false;
				}
			}
		} catch (IOException e) {
			return true;
		}
	}

	private static byte[] inbuf = new byte[1024];
	static int lenbuf = 0, ptrbuf = 0;

	private static int readByte()
	{
		if(lenbuf == -1)throw new InputMismatchException();
		if(ptrbuf >= lenbuf){
			ptrbuf = 0;
			try { lenbuf = is.read(inbuf); } catch (IOException e) { throw new InputMismatchException(); }
			if(lenbuf <= 0)return -1;
		}
		return inbuf[ptrbuf++];
	}

	private static boolean isSpaceChar(int c) { return !(c >= 33 && c <= 126); }
//	private static boolean isSpaceChar(int c) { return !(c >= 32 && c <= 126); }
	private static int skip() { int b; while((b = readByte()) != -1 && isSpaceChar(b)); return b; }

	private static double nd() { return Double.parseDouble(ns()); }
	private static char nc() { return (char)skip(); }

	private static String ns()
	{
		int b = skip();
		StringBuilder sb = new StringBuilder();
		while(!(isSpaceChar(b))){
			sb.appendCodePoint(b);
			b = readByte();
		}
		return sb.toString();
	}

	private static char[] ns(int n)
	{
		char[] buf = new char[n];
		int b = skip(), p = 0;
		while(p < n && !(isSpaceChar(b))){
			buf[p++] = (char)b;
			b = readByte();
		}
		return n == p ? buf : Arrays.copyOf(buf, p);
	}

	private static char[][] nm(int n, int m)
	{
		char[][] map = new char[n][];
		for(int i = 0;i < n;i++)map[i] = ns(m);
		return map;
	}

	private static int[] na(int n)
	{
		int[] a = new int[n];
		for(int i = 0;i < n;i++)a[i] = ni();
		return a;
	}

	private static int ni()
	{
		int num = 0, b;
		boolean minus = false;
		while((b = readByte()) != -1 && !((b >= '0' && b <= '9') || b == '-'));
		if(b == '-'){
			minus = true;
			b = readByte();
		}

		while(true){
			if(b >= '0' && b <= '9'){
				num = num * 10 + (b - '0');
			}else{
				return minus ? -num : num;
			}
			b = readByte();
		}
	}

	private static long nl()
	{
		long num = 0;
		int b;
		boolean minus = false;
		while((b = readByte()) != -1 && !((b >= '0' && b <= '9') || b == '-'));
		if(b == '-'){
			minus = true;
			b = readByte();
		}

		while(true){
			if(b >= '0' && b <= '9'){
				num = num * 10 + (b - '0');
			}else{
				return minus ? -num : num;
			}
			b = readByte();
		}
	}

	private static void tr(Object... o) { if(INPUT.length() != 0)System.out.println(Arrays.deepToString(o)); }
}

import java.io.OutputStream;
import java.io.IOException;
import java.io.InputStream;
import java.io.OutputStream;
import java.io.PrintWriter;
import java.util.Arrays;
import java.io.BufferedWriter;
import java.io.Writer;
import java.io.OutputStreamWriter;
import java.util.InputMismatchException;
import java.io.IOException;
import java.util.BitSet;
import java.util.Comparator;
import java.io.InputStream;

/**
 * Built using CHelper plug-in
 * Actual solution is at the top
 */
public class Main {
    public static void main(String[] args) {
        InputStream inputStream = System.in;
        OutputStream outputStream = System.out;
        InputReader in = new InputReader(inputStream);
        OutputWriter out = new OutputWriter(outputStream);
        TaskE solver = new TaskE();
        solver.solve(1, in, out);
        out.close();
    }

    static class TaskE {
        public void solve(int testNumber, InputReader in, OutputWriter out) {
            int n = in.readInt();
            int m = in.readInt();
            int[][] a = in.readIntTable(n, m);

            BitSet[] data = new BitSet[n];
            for (int i = 0; i < n; i++) {
                data[i] = new BitSet();
            }

            for (int i = 0; i < n; i++) {
                for (int j = 0; j < m; j++) {
                    if (a[i][j] == 1) data[i].set(j);
                }
            }

            int idx = 0;
            for (int i = m - 1; i >= 0 && idx < n; i--) {
                Arrays.sort(data, Comparator.comparing(BitSet::length).reversed());
                if (!data[idx].get(i)) continue;

                for (int j = 0; j < n; j++) {
                    if (j != idx && data[j].get(i)) data[j].xor(data[idx]);
                }

                idx++;
            }

            int rank = 0;
            for (int i = 0; i < n; i++) {
                if (!data[i].isEmpty()) rank++;
            }

            Modulo modulo = new Modulo(998244353);
            long tmp1 = modulo.pow(2, n);
            long tmp2 = modulo.pow(2, n - rank);
            long tmp3 = modulo.pow(2, m - 1);

            out.printLine(modulo.mlt(modulo.sub(tmp1, tmp2), tmp3));
        }

    }

    static class OutputWriter {
        private final PrintWriter writer;

        public OutputWriter(OutputStream outputStream) {
            writer = new PrintWriter(new BufferedWriter(new OutputStreamWriter(outputStream)));
        }

        public OutputWriter(Writer writer) {
            this.writer = new PrintWriter(writer);
        }

        public void close() {
            writer.close();
        }

        public void printLine(long i) {
            writer.println(i);
        }

    }

    static class Modulo {
        long m;

        public Modulo(long m) {
            this.m = m;
        }

        public long sub(long a, long b) {
            return (a - b + m) % m;
        }

        public long mlt(long a, long b) {
            return (a * b) % m;
        }

        public long pow(long a, long x) {
            long ans = 1 % m;
            while (x > 0) {
                if ((x & 1) != 0) {
                    ans = (ans * a) % m;
                }
                x >>= 1;
                a = (a * a) % m;
            }
            return ans;
        }

    }

    static class InputReader {
        private InputStream stream;
        private byte[] buf = new byte[1024];
        private int curChar;
        private int numChars;
        private InputReader.SpaceCharFilter filter;

        public InputReader(InputStream stream) {
            this.stream = stream;
        }

        public int[][] readIntTable(int rowCount, int columnCount) {
            int[][] table = new int[rowCount][];
            for (int i = 0; i < rowCount; i++) {
                table[i] = readIntArray(columnCount);
            }
            return table;
        }

        public int[] readIntArray(int size) {
            int[] array = new int[size];
            for (int i = 0; i < size; i++) {
                array[i] = readInt();
            }
            return array;
        }

        public int read() {
            if (numChars == -1) {
                throw new InputMismatchException();
            }
            if (curChar >= numChars) {
                curChar = 0;
                try {
                    numChars = stream.read(buf);
                } catch (IOException e) {
                    throw new InputMismatchException();
                }
                if (numChars <= 0) {
                    return -1;
                }
            }
            return buf[curChar++];
        }

        public int readInt() {
            int c = read();
            while (isSpaceChar(c)) {
                c = read();
            }
            int sgn = 1;
            if (c == '-') {
                sgn = -1;
                c = read();
            }
            int res = 0;
            do {
                if (c < '0' || c > '9') {
                    throw new InputMismatchException();
                }
                res *= 10;
                res += c - '0';
                c = read();
            } while (!isSpaceChar(c));
            return res * sgn;
        }

        public boolean isSpaceChar(int c) {
            if (filter != null) {
                return filter.isSpaceChar(c);
            }
            return isWhitespace(c);
        }

        public static boolean isWhitespace(int c) {
            return c == ' ' || c == '\n' || c == '\r' || c == '\t' || c == -1;
        }

        public interface SpaceCharFilter {
            public boolean isSpaceChar(int ch);

        }

    }
}

import java.util.Scanner;

public class Main {
	static Scanner sc = new Scanner(System.in);
	static final long MOD = 998244353;
	static int N, M;

	public static void main(String[] args) {
		N = sc.nextInt();
		M = sc.nextInt();
		int[][] mat = new int[N][M];
		for (int i = 0; i < N; i++) {
			for (int j = 0; j < M; j++) {
				mat[i][j] = sc.nextInt();
			}
		}
		int dim = 0;
		int col = 0;
		boolean[] fix = new boolean[N];
		for (int i = 0; i < N && col < M; i++) {
			for (int j = 0; j < N; j++) {
				if (!fix[j] && mat[j][col] == 1) {
					for (int k = 0; k < N; k++) {
						if (k == j || mat[k][col] == 0) continue;
						for (int l = 0; l < M; l++) {
							mat[k][l] ^= mat[j][l];
						}
					}
					++dim;
					fix[j] = true;
					break;
				}
			}
			++col;
		}
//		for (int i = 0; i < N; i++) {
//			for (int j = 0; j < M; j++) {
//				System.err.print(mat[i][j] + " ");
//			}
//			System.err.println();
//		}

		long[] pow2 = new long[301];
		pow2[0] = 1;
		for (int i = 1; i < pow2.length; i++) {
			pow2[i] = pow2[i - 1] * 2 % MOD;
		}
		long[][] comb = new long[301][301];
		for (int i = 0; i < comb.length; i++) {
			comb[i][0] = comb[i][i] = 1;
		}
		for (int i = 0; i < comb.length; i++) {
			for (int j = 1; j < i; j++) {
				comb[i][j] = (comb[i - 1][j] + comb[i - 1][j - 1]) % MOD;
			}
		}
		long ans = 0;
		for (int i = 1; i <= dim; i++) {
			long here = 0;
			for (int j = 1; j <= i; j += 2) {
				here += comb[i][j];
				here %= MOD;
			}
			here *= comb[dim][i];
			here %= MOD;
			here *= pow2[M - i];
			here %= MOD;
			ans += here;
		}
		ans %= MOD;
		ans *= pow2[N - dim];
		ans %= MOD;
		System.out.println(ans);
	}

}

