    static class EOddSubrectangles {
        int mod = 998244353;

        long pow(long a, long p, int mod) {
            if (p == 0) {
                return 1;
            }

            long t = pow(a, p / 2, mod);

            if (p % 2 != 0) {
                return (((t * t) % mod) * a) % mod;
            } else {
                return (t * t) % mod;
            }
        }

        public void solve(int testNumber, InputReader in, OutputWriter out) {

            int n = in.nextInt();
            int m = in.nextInt();
            int[][] a = new int[n][m];

            for (int i = 0; i < n; i++) {
                for (int j = 0; j < m; j++) {
                    a[i][j] = in.nextInt();
                }
            }

            MatrixRank rank = new MatrixRank();

            int r = rank.matrixRankFF2(a);

            long ans = pow(2, n + m - 1, mod);
            ans -= pow(2, n + m - r - 1, mod);

            ans %= mod;

            if (ans < 0) {
                ans += mod;
            }

            out.println(ans);
        }

        class MatrixRank {
            int matrixRankFF2(int[][] a) {
                int n = a.length;
                int m = a[0].length;

                Arrays.sort(a, (row1, row2) ->
                {
                    for (int i = 0; i < m; i++) {
                        if (row1[i] > row2[i]) {
                            return -1;
                        } else if (row1[i] < row2[i]) {
                            return 1;
                        }
                    }
                    return 0;
                });

                int rank = 0;

                for (int i = 0; i < n; i++) {
                    int fsi = -1;
                    for (int j = 0; j < m; j++) {
                        if (a[i][j] == 1) {
                            fsi = j;
                            break;
                        }
                    }

                    if (fsi == -1) {
                        return rank;
                    }

                    rank++;

                    for (int j = i + 1; j < n; j++) {
                        if (a[j][fsi] == 1) {
                            for (int k = fsi; k < m; k++) {
                                a[j][k] = 1 - a[j][k];
                            }
                        }
                    }

                    Arrays.sort(a, (row1, row2) ->
                    {
                        for (int j = 0; j < m; j++) {
                            if (row1[j] > row2[j]) {
                                return -1;
                            } else if (row1[j] < row2[j]) {
                                return 1;
                            }
                        }
                        return 0;
                    });
                }

                return rank;
            }

        }

    }

