  private static long solve2(int[][] mat) {
    int n = mat.length;
    int m = mat[0].length;

    int ret = 0;
    for (int i = 0; i < (1 << n); i++) {
      for (int j = 0; j < (1 << m); j++) {

        int sum = 0;
        for (int p = 0; p < n; p++) {
          if (((i >> p) & 1) == 0)
            continue;
          for (int q = 0; q < m; q++) {
            if (((j >> q) & 1) == 0)
              continue;

            sum += mat[p][q];
          }
        }
        if (sum % 2 == 1) {
          ret++;
        }
      }
    }
    return ret;
  }

  private static long solve(int[][] mat) {

    int n = mat.length;
    int m = mat[0].length;

    int rank = rank(mat, 2);

    int mod = 998244353;
    long[] p2 = new long[1000];
    p2[0] = 1;
    for (int i = 0; i < p2.length - 1; i++) {
      p2[i + 1] = p2[i] * 2 % mod;
    }

    int[][] fif = enumFIF(1000, mod);

    long ret = 0;
    for (int i = 1; i <= rank; i += 2) {
      long now = CX(rank, i, mod, fif) * ((p2[rank - i] + p2[rank - i] - 1) % mod) % mod;
      ret += now * p2[n - rank] % mod * p2[m - rank] % mod;
      ret %= mod;
    }
    if (ret < 0) ret += mod;
    return ret;
  }

  public static long CX(long n, long r, int p, int[][] fif) {
    if (n < 0 || r < 0 || r > n)
      return 0;
    int np = (int) (n % p), rp = (int) (r % p);
    if (np < rp)
      return 0;
    if (n == 0 && r == 0)
      return 1;
    int nrp = np - rp;
    if (nrp < 0)
      nrp += p;
    return (long) fif[0][np] * fif[1][rp] % p * fif[1][nrp] % p * CX(n / p, r / p, p, fif) % p;
  }

  public static int[][] enumFIF(int n, int mod) {
    int[] f = new int[n + 1];
    int[] invf = new int[n + 1];
    f[0] = 1;
    for (int i = 1; i <= n; i++) {
      f[i] = (int) ((long) f[i - 1] * i % mod);
    }
    long a = f[n];
    long b = mod;
    long p = 1, q = 0;
    while (b > 0) {
      long c = a / b;
      long d;
      d = a;
      a = b;
      b = d % b;
      d = p;
      p = q;
      q = d - c * q;
    }
    invf[n] = (int) (p < 0 ? p + mod : p);
    for (int i = n - 1; i >= 0; i--) {
      invf[i] = (int) ((long) invf[i + 1] * (i + 1) % mod);
    }
    return new int[][] {f, invf};
  }

  public static int rank(int[][] M, int p) {
    if (M.length == 0)
      return 0;
    int n = M.length, m = M[0].length;

    // Forward Elimination
    for (int i = 0; i < n; i++) {
      // select pivot
      boolean pivotFound = false;
      out: for (int pi = i; pi < n; pi++) {
        for (int pj = i; pj < m; pj++) {
          if (M[pi][pj] != 0) {
            // pivot found
            if (pj != i) {
              // swap columns
              for (int k = 0; k < n; k++) {
                int d = M[k][pj];
                M[k][pj] = M[k][i];
                M[k][i] = d;
              }
            }
            if (pi != i) {
              // swap rows
              int[] d = M[pi];
              M[pi] = M[i];
              M[i] = d;
            }
            pivotFound = true;
            break out;
          }
        }
      }
      if (!pivotFound)
        return i;

      long ID = invl(M[i][i], p);
      M[i][i] = 1;
      for (int j = i + 1; j < m; j++) {
        M[i][j] = (int) (M[i][j] * ID % p);
      }

      for (int j = i + 1; j < n; j++) {
        long B = p - M[j][i];
        M[j][i] = 0;
        for (int k = i + 1; k < m; k++) {
          M[j][k] = (int) ((M[j][k] + M[i][k] * B) % p);
        }
      }
    }
    return n;
  }

  public static long invl(long a, long mod) {
    long b = mod;
    long p = 1, q = 0;
    while (b > 0) {
      long c = a / b;
      long d;
      d = a;
      a = b;
      b = d % b;
      d = p;
      p = q;
      q = d - c * q;
    }
    return p < 0 ? p + mod : p;
  }

