    int np = (int) (n % p), rp = (int) (r % p);
    if (np < rp)
      return 0;
    if (n == 0 && r == 0)
      return 1;
    int nrp = np - rp;
    if (nrp < 0)
      nrp += p;
    return (long) fif[0][np] * fif[1][rp] % p * fif[1][nrp] % p * CX(n / p, r / p, p, fif) % p;
  }

  public static int[][] enumFIF(int n, int mod) {
    int[] f = new int[n + 1];
    int[] invf = new int[n + 1];
    f[0] = 1;
    for (int i = 1; i <= n; i++) {
      f[i] = (int) ((long) f[i - 1] * i % mod);
    }
    long a = f[n];
    long b = mod;
    long p = 1, q = 0;
    while (b > 0) {
      long c = a / b;
      long d;
      d = a;
      a = b;
      b = d % b;
      d = p;
      p = q;
      q = d - c * q;
    }
    invf[n] = (int) (p < 0 ? p + mod : p);
    for (int i = n - 1; i >= 0; i--) {
      invf[i] = (int) ((long) invf[i + 1] * (i + 1) % mod);
    }
    return new int[][] {f, invf};
  }

  public static int rank(int[][] M, int p) {
    if (M.length == 0)
      return 0;
    int n = M.length, m = M[0].length;

    // Forward Elimination
    for (int i = 0; i < n; i++) {
      // select pivot
      boolean pivotFound = false;
      out: for (int pi = i; pi < n; pi++) {
        for (int pj = i; pj < m; pj++) {
          if (M[pi][pj] != 0) {
            // pivot found
            if (pj != i) {
              // swap columns
              for (int k = 0; k < n; k++) {
                int d = M[k][pj];
                M[k][pj] = M[k][i];
                M[k][i] = d;
              }
            }
            if (pi != i) {
              // swap rows
              int[] d = M[pi];
              M[pi] = M[i];
              M[i] = d;
            }
            pivotFound = true;
            break out;
          }
        }
