    static class FastInput {
        private final InputStream is;
        private byte[] buf = new byte[1 << 13];
        private int bufLen;
        private int bufOffset;
        private int next;

        public FastInput(InputStream is) {
            this.is = is;
        }

        private int read() {
            while (bufLen == bufOffset) {
                bufOffset = 0;
                try {
                    bufLen = is.read(buf);
                } catch (IOException e) {
                    bufLen = -1;
                }
                if (bufLen == -1) {
                    return -1;
                }
            }
            return buf[bufOffset++];
        }

        public void skipBlank() {
            while (next >= 0 && next <= 32) {
                next = read();
            }
        }

        public int readInt() {
            int sign = 1;

            skipBlank();
            if (next == '+' || next == '-') {
                sign = next == '+' ? 1 : -1;
                next = read();
            }

            int val = 0;
            if (sign == 1) {
                while (next >= '0' && next <= '9') {
                    val = val * 10 + next - '0';
                    next = read();
                }
            } else {
                while (next >= '0' && next <= '9') {
                    val = val * 10 - next + '0';
                    next = read();
                }
            }

            return val;
        }

        public char readChar() {
            skipBlank();
            char c = (char) next;
            next = read();
            return c;
        }

    }

    static class GenericLinearBasis {
        private BitSet[] basis;
        private int size;
        private int dimension;
        private BitSet or;
        private BitSet buf;

        public GenericLinearBasis(int dimension) {
            this.dimension = dimension;
            basis = new BitSet[dimension];
            for (int i = 0; i < dimension; i++) {
                basis[i] = new BitSet(dimension);
            }
            or = new BitSet(dimension);
            buf = new BitSet(dimension);
        }

        public void add(BitSet bits) {
            buf.copy(bits);
            bits = buf;
            for (int i = dimension - 1; i >= 0; i--) {
                if (!bits.get(i)) {
                    continue;
                }
                if (or.get(i)) {
                    bits.xor(basis[i]);
                    continue;
                }
                or.set(i);
                size++;
                basis[i].copy(bits);
                for (int j = i + 1; j < dimension; j++) {
                    if (!or.get(j) || !basis[j].get(i)) {
                        continue;
                    }
                    basis[j].xor(basis[i]);
                }
                return;
            }
        }

        public int size() {
            return size;
        }

    }

