                        continue;

                    s.add(ind);
                    rank++;

                    for (int j = ind + 1; j < n; j++) {
                        if (a[j][i] == 1) {
                            for (int k = i; k < m; k++) {
                                a[j][k] ^= a[ind][k];
                            }
                        }
                    }
                }

                return rank;
            }

        }

    }

