import java.util.*;
import java.io.*;

import static java.lang.System.in;

public class Main {
    static int n,m;
    static long mod = 998244353;
    public static void main(String[] args)throws IOException{
        Scanner sc = new Scanner(System.in);
        n = sc.nextInt(); m = sc.nextInt();
        int[][] a= new int[n][m];
        for(int i=0;i<n;i++){
            for(int j=0;j<m;j++) a[i][j]=sc.nextInt();
        }
        int rank = getRank(a);
        long ans = (power(2,n+m-1)-power(2,n+m-rank-1)+mod)%mod;
        System.out.println(ans);
    }
    static long power(long base, long q){
        long ans = 1;
        while(q>0){
            if(q%2==1) ans = ans*base%mod;
            base = base*base%mod;
            q /= 2;
        }
        return ans;
    }
    static int getRank(int[][] A){
        int rank = 0;
        for(int j=0;j<m;j++){
            int pivot = -1;
            for(int i=rank;i<n;i++){
                if(A[i][j]>0){
                    pivot = i;
                    break;
                }
            }
            if(pivot<0) continue; // following col may still contains non-zero. need to go on
            if(pivot!=rank){ // swap the chosen line to the top of the concerned area
                for(int k=0;k<m;k++){
                    int c = A[pivot][k];
                    A[pivot][k] = A[rank][k];
                    A[rank][k] = c;
                }
            }
            for(int i=rank+1;i<n;i++){
                if(A[i][j]==0) continue;
                for(int k=j;k<m;k++) A[i][j]^=A[rank][k];
            }
            rank++;
        }
        return rank;
    }
}

public class Main {



  private static void solve() {
     int n = ni();
     int m = ni();
     int[][] mat = ntable(n, m);

     System.out.println(solve(mat));
  }


  private static long solve(int[][] mat) {

    int n = mat.length;
    int m = mat[0].length;

    int rank = rank(mat, 2);

    int mod = 998244353;
    long[] p2 = new long[1000];
    p2[0] = 1;
    for (int i = 0; i < p2.length - 1; i++) {
      p2[i + 1] = p2[i] * 2 % mod;
    }

    int[][] fif = enumFIF(1000, mod);

    long ret = 0;
    for (int i = 1; i <= rank; i ++) {
      long now = 0;
      for (int j = 1; j <= i; j += 2) {
        now += CX(rank, i, mod, fif) * CX(i, j, mod, fif) % mod;
        now %= mod;
      }
      ret += now * p2[m - i] % mod * p2[n - rank] % mod ;
      ret %= mod;
    }
    return ret;
  }

  public static long CX(long n, long r, int p, int[][] fif) {
    if (n < 0 || r < 0 || r > n)
      return 0;
    int np = (int) (n % p), rp = (int) (r % p);
    if (np < rp)
      return 0;
    if (n == 0 && r == 0)
      return 1;
    int nrp = np - rp;
    if (nrp < 0)
      nrp += p;
    return (long) fif[0][np] * fif[1][rp] % p * fif[1][nrp] % p * CX(n / p, r / p, p, fif) % p;
  }

  public static int[][] enumFIF(int n, int mod) {
    int[] f = new int[n + 1];
    int[] invf = new int[n + 1];
    f[0] = 1;
    for (int i = 1; i <= n; i++) {
      f[i] = (int) ((long) f[i - 1] * i % mod);
    }
    long a = f[n];
    long b = mod;
    long p = 1, q = 0;
    while (b > 0) {
      long c = a / b;
      long d;
      d = a;
      a = b;
      b = d % b;
      d = p;
      p = q;
      q = d - c * q;
    }
    invf[n] = (int) (p < 0 ? p + mod : p);
    for (int i = n - 1; i >= 0; i--) {
      invf[i] = (int) ((long) invf[i + 1] * (i + 1) % mod);
    }
    return new int[][] {f, invf};
  }

  public static int rank(int[][] M, int p) {
    if (M.length == 0)
      return 0;
    int n = M.length, m = M[0].length;

    // Forward Elimination
    for (int i = 0; i < n; i++) {
      // select pivot
      boolean pivotFound = false;
      out: for (int pi = i; pi < n; pi++) {
        for (int pj = i; pj < m; pj++) {
          if (M[pi][pj] != 0) {
            // pivot found
            if (pj != i) {
              // swap columns
              for (int k = 0; k < n; k++) {
                int d = M[k][pj];
                M[k][pj] = M[k][i];
                M[k][i] = d;
              }
            }
            if (pi != i) {
              // swap rows
              int[] d = M[pi];
              M[pi] = M[i];
              M[i] = d;
            }
            pivotFound = true;
            break out;
          }
        }
      }
      if (!pivotFound)
        return i;

      long ID = invl(M[i][i], p);
      M[i][i] = 1;
      for (int j = i + 1; j < m; j++) {
        M[i][j] = (int) (M[i][j] * ID % p);
      }

      for (int j = i + 1; j < n; j++) {
        long B = p - M[j][i];
        M[j][i] = 0;
        for (int k = i + 1; k < m; k++) {
          M[j][k] = (int) ((M[j][k] + M[i][k] * B) % p);
        }
      }
    }
    return n;
  }

  public static long invl(long a, long mod) {
    long b = mod;
    long p = 1, q = 0;
    while (b > 0) {
      long c = a / b;
      long d;
      d = a;
      a = b;
      b = d % b;
      d = p;
      p = q;
      q = d - c * q;
    }
    return p < 0 ? p + mod : p;
  }

  public static void main(String[] args) {
    new Thread(null, new Runnable() {
      @Override
      public void run() {
        long start = System.currentTimeMillis();
        String debug = args.length > 0 ? args[0] : null;
        if (debug != null) {
          try {
            is = java.nio.file.Files.newInputStream(java.nio.file.Paths.get(debug));
          } catch (Exception e) {
            throw new RuntimeException(e);
          }
        }
        reader = new java.io.BufferedReader(new java.io.InputStreamReader(is), 32768);
        solve();
        out.flush();
        tr((System.currentTimeMillis() - start) + "ms");
      }
    }, "", 64000000).start();
  }

  private static java.io.InputStream is = System.in;
