/**
 * Built using CHelper plug-in
 * Actual solution is at the top
 */
public class Main {
	public static void main(String[] args) {
		InputStream inputStream = System.in;
		OutputStream outputStream = System.out;
		FastScanner in = new FastScanner(inputStream);
		PrintWriter out = new PrintWriter(outputStream);
		E_OddSubrectangles solver = new E_OddSubrectangles();
		solver.solve(1, in, out);
		out.close();
	}

	static class E_OddSubrectangles {
		public void solve(int testNumber, FastScanner in, PrintWriter out) {
			final int MOD = 998244353;
			int height = in.nextInt();
			int width = in.nextInt();
			BitSet[] a = new BitSet[height];
			for (int r = 0; r < height; r++) {
				a[r] = new BitSet(width);
				for (int c = 0; c < width; c++) {
					int x = in.nextInt();
					if (x == 1) {
						a[r].set(c);
					}
				}
			}

			int rank = calcRank(a.clone());

			long[] p2 = new long[height + width + 1];
			p2[0] = 1;
			for (int i = 1; i < p2.length; i++) {
				p2[i] = 2 * p2[i - 1] % MOD;
			}
			long ans = p2[height + width - 1] - p2[height + width - 1 - rank];
			if (ans < 0) {
				ans += MOD;
			}
			out.println(ans);
		}

		private int calcRank(BitSet[] rows) {
			diagonalize(rows);
			int res = 0;
			for (BitSet row : rows) {
				if (row.cardinality() != 0) {
					++res;
				}
			}
			return res;
		}

		private void diagonalize(BitSet[] a) {
			int lastR = a.length - 1;
			int r = 0;
			while (r <= lastR) {
				if (a[r].cardinality() == 0) {
					BitSet t = a[r];
					a[r] = a[lastR];
					a[lastR] = t;
					--lastR;
					continue;
				}

				int or = a[r].nextSetBit(r);
				if (or != r) {
					for (int i = 0; i <= lastR; i++) {
						boolean bitR = a[i].get(r);
						boolean bitOR = a[i].get(or);
						a[i].set(r, bitOR);
						a[i].set(or, bitR);
					}
				}
				for (int i = r + 1; i <= lastR; i++) {
					if (a[i].get(r)) {
						a[i].xor(a[r]);
					}
				}

				++r;
			}
		}

	}

	static class FastScanner {
		private BufferedReader in;
		private StringTokenizer st;

		public FastScanner(InputStream stream) {
			in = new BufferedReader(new InputStreamReader(stream));
		}

		public String next() {
			while (st == null || !st.hasMoreTokens()) {
				try {
					String rl = in.readLine();
					if (rl == null) {
						return null;
					}
					st = new StringTokenizer(rl);
				} catch (IOException e) {
					throw new RuntimeException(e);
				}
			}
			return st.nextToken();
		}

		public int nextInt() {
			return Integer.parseInt(next());
		}

	}
}

import java.util.*;
public class Main {
	public static class bitset {
		long[] a=new long[8];
		public void set(long v,int id) {
			a[id/50]|=v<<(id%50);
		}
		public int get(int id) {
			return (int)((a[id/50]>>(id%50))&1);
		}
		public void xor(bitset rhs) {
			for (int i=0;i<8;++i) a[i]^=rhs.a[i];
		}
	}
	public static void main(String[] args) {
		Scanner in=new Scanner(System.in);
		int n=in.nextInt();
		int m=in.nextInt();
		bitset[] bit=new bitset[n];
		for (int i=0;i<n;++i) {
			bit[i]=new bitset();
			for (int j=0;j<m;++j) {
				int x=in.nextInt();
				bit[i].set(x,j);
			}
		}
		long mod=998244353;
		long[] p=new long[n+m];
		p[0]=1;
		for (int i=1;i<n+m;++i) p[i]=(p[i-1]+p[i-1])%mod;
		int rk=0;
		for (int i=0;i<m;++i) {
			boolean flag=false;
			for (int j=i;j<n;++j) if (bit[j].get(i)==1) {
				bitset tmp=bit[j];
				bit[j]=bit[i];
				bit[i]=tmp;
				flag=true;
			}
			if (!flag) continue;
			++rk;
			for (int j=i+1;j<n;++j) if (bit[j].get(i)==1)
				bit[j].xor(bit[i]);
		}
		System.out.println((p[n+m-1]-p[n+m-1-rk]+mod)%mod);
	}
}

public class Main {

  private static void solve() {
    int n = ni();
    int m = ni();

    int mod = 998244353;
    int[][] p = ntable(n, m);

    int rank = rank(p, 2);

    int[][] fif = enumFIF(n + m, mod);

    long ret = 0;
    for (int i = 1; i <= rank; i += 2) {
      ret += C(rank, i, mod, fif) * pow(2, (n - rank) + (m - rank), mod) % mod
          * pow(3, rank - i, mod) % mod;
      ret %= mod;
    }
    System.out.println(ret);
  }

  public static long C(int n, int r, int mod, int[][] fif) {
    if (n < 0 || r < 0 || r > n)
      return 0;
    return (long) fif[0][n] * fif[1][r] % mod * fif[1][n - r] % mod;
  }


  public static int[][] enumFIF(int n, int mod) {
    int[] f = new int[n + 1];
    int[] invf = new int[n + 1];
    f[0] = 1;
    for (int i = 1; i <= n; i++) {
      f[i] = (int) ((long) f[i - 1] * i % mod);
    }
    long a = f[n];
    long b = mod;
    long p = 1, q = 0;
    while (b > 0) {
      long c = a / b;
      long d;
      d = a;
      a = b;
      b = d % b;
      d = p;
      p = q;
      q = d - c * q;
    }
    invf[n] = (int) (p < 0 ? p + mod : p);
    for (int i = n - 1; i >= 0; i--) {
      invf[i] = (int) ((long) invf[i + 1] * (i + 1) % mod);
    }
    return new int[][] {f, invf};
  }


  public static long pow(long a, long n, long mod) {
    // a %= mod;
    long ret = 1;
    int x = 63 - Long.numberOfLeadingZeros(n);
    for (; x >= 0; x--) {
      ret = ret * ret % mod;
      if (n << 63 - x < 0)
        ret = ret * a % mod;
    }
    return ret;
  }


  public static int rank(int[][] M, int p) {
    if (M.length == 0)
      return 0;
    int n = M.length, m = M[0].length;

    // Forward Elimination
    for (int i = 0; i < n; i++) {
      // select pivot
      boolean pivotFound = false;
      out: for (int pi = i; pi < n; pi++) {
        for (int pj = i; pj < m; pj++) {
          if (M[pi][pj] != 0) {
            // pivot found
            if (pj != i) {
              // swap columns
              for (int k = 0; k < n; k++) {
                int d = M[k][pj];
                M[k][pj] = M[k][i];
                M[k][i] = d;
              }
            }
            if (pi != i) {
              // swap rows
              int[] d = M[pi];
              M[pi] = M[i];
              M[i] = d;
            }
            pivotFound = true;
            break out;
          }
        }
      }
      if (!pivotFound)
        return i;

      long ID = invl(M[i][i], p);
      M[i][i] = 1;
      for (int j = i + 1; j < m; j++) {
        M[i][j] = (int) (M[i][j] * ID % p);
      }

      for (int j = i + 1; j < n; j++) {
        long B = p - M[j][i];
        M[j][i] = 0;
        for (int k = i + 1; k < m; k++) {
          M[j][k] = (int) ((M[j][k] + M[i][k] * B) % p);
        }
      }
    }
    return n;
  }

  public static long invl(long a, long mod) {
    long b = mod;
    long p = 1, q = 0;
    while (b > 0) {
      long c = a / b;
      long d;
      d = a;
      a = b;
      b = d % b;
      d = p;
      p = q;
      q = d - c * q;
    }
    return p < 0 ? p + mod : p;
  }


