    static class FastInput {
        private final InputStream is;
        private byte[] buf = new byte[1 << 13];
        private int bufLen;
        private int bufOffset;
        private int next;

        public FastInput(InputStream is) {
            this.is = is;
        }

        private int read() {
            while (bufLen == bufOffset) {
                bufOffset = 0;
                try {
                    bufLen = is.read(buf);
                } catch (IOException e) {
                    bufLen = -1;
                }
                if (bufLen == -1) {
                    return -1;
                }
            }
            return buf[bufOffset++];
        }

        public void skipBlank() {
            while (next >= 0 && next <= 32) {
                next = read();
            }
        }

        public int readInt() {
            int sign = 1;

            skipBlank();
            if (next == '+' || next == '-') {
                sign = next == '+' ? 1 : -1;
                next = read();
            }

            int val = 0;
            if (sign == 1) {
                while (next >= '0' && next <= '9') {
                    val = val * 10 + next - '0';
                    next = read();
                }
            } else {
                while (next >= '0' && next <= '9') {
                    val = val * 10 - next + '0';
                    next = read();
                }
            }

            return val;
        }

        public char readChar() {
            skipBlank();
            char c = (char) next;
            next = read();
            return c;
        }

    }

    static class GeneralizedLinearBasis {
        private boolean[][] basis;
        private int size;
        private int dimension;
        private boolean[] or;

        public GeneralizedLinearBasis(int dimension) {
            this.dimension = dimension;
            basis = new boolean[dimension][dimension];
            or = new boolean[dimension];
        }

        public void add(boolean[] bits) {
            for (int i = dimension - 1; i >= 0; i--) {
                if (!bits[i]) {
                    continue;
                }
                if (or[i]) {
                    for (int j = 0; j < dimension; j++) {
                        bits[i] = bits[j] != basis[i][j];
                    }
                    continue;
                }
                or[i] = true;
                size++;
                System.arraycopy(bits, 0, basis[i], 0, dimension);
                for (int j = i + 1; j < dimension; j++) {
                    if (!or[j] || !basis[j][i]) {
                        continue;
                    }
                    for (int k = 0; k < dimension; k++) {
                        basis[j][k] = basis[j][k] != basis[i][k];
                    }
                }
                return;
            }
        }

        public int size() {
            return size;
        }

    }
}

