class BitVector
{
    readonly int N;
    readonly ulong[] bits;
    public bool this[int i] => (bits[i / 64] & (1ul << (i % 64))) != 0;
    public BitVector(int n) { N = n; bits = new ulong[(n + 63) / 64]; }
    public BitVector(bool[] b) : this(b.Length)
    {
        for (var i = 0; i < N; i++) if (b[i]) bits[i / 64] |= 1ul << (i % 64);
    }
    public void Add(BitVector v)
    {
        for (var i = 0; i < bits.Length; i++) bits[i] ^= v.bits[i];
    }
    public static BitVector operator ^(BitVector a, BitVector b)
    {
        var c = new BitVector(a.N);
        for (var i = 0; i < a.bits.Length; i++) c.bits[i] = a.bits[i] ^ b.bits[i];
        return c;
    }
    public static BitVector operator |(BitVector a, BitVector b)
    {
        var c = new BitVector(a.N);
        for (var i = 0; i < a.bits.Length; i++) c.bits[i] = a.bits[i] | b.bits[i];
        return c;
    }
    public static BitVector operator &(BitVector a, BitVector b)
    {
        var c = new BitVector(a.N);
        for (var i = 0; i < a.bits.Length; i++) c.bits[i] = a.bits[i] & b.bits[i];
        return c;
    }
    public static BitVector operator !(BitVector a)
    {
        var c = new BitVector(a.N);
        for (var i = 0; i < a.bits.Length; i++) c.bits[i] = ~a.bits[i];
        return c;
    }
}

