namespace ConsoleApp51
{
    class Program
    {
        static void Main(string[] args)
        {
            int[] dm = Console.ReadLine().Split().Select(x => int.Parse(x)).ToArray();
            int[][] mt = new int[dm[0]][];
            for (int i = 0; i < dm[0]; ++i)
                mt[i] = Console.ReadLine().Split().Select(x => int.Parse(x)).ToArray();
            Solution sol = new Solution();
            Console.WriteLine(sol.GetOddRectangles(mt, dm[0], dm[1]));
        }
    }


    public class Solution
    {
        public int GetOddRectangles(int[][] mt, int N, int M)
        {
            int[,] acc = new int[N + 1, M + 1];

            for (int i = 1; i <= N; ++i)
                for (int j = 1; j <= M; ++j)
                    acc[i, j] = acc[i - 1, j] + acc[i, j - 1] - acc[i - 1, j - 1] + mt[i - 1][j - 1];

            int sol = 0;
            for (int i = 1; i <= N; ++i)
                for (int j = 1; j <= M; ++j)
                    for (int l = i; l <= N; ++l)
                        for (int k = j; k <= M; ++k)
                            sol += (acc[l, k] - acc[l, j - 1] - acc[i - 1, k] + acc[i - 1, j - 1]) & 1;
            return sol % 998244353;
        }
    }
}

