class BitVector
{
    readonly int N;
    readonly byte[] bits;
    public bool this[int i] => (bits[i / 8] & (1u << (i % 8))) != 0;
    public BitVector(bool[] b)
    {
        N = b.Length;
        bits = new byte[(N + 7) / 8];
        for (var i = 0; i < N; i++) if (b[i]) bits[i / 8] |= (byte)(1u << (i % 8));
    }
    public void Add(BitVector v)
    {
        for (var i = 0; i < bits.Length; i++) bits[i] ^= v.bits[i];
    }
}

