package main

import "fmt"

var ymap map[int]int
var table [][]int

func init() {
	ymap = map[int]int{}
}

func getRow(idx, left, right int) int {
	k := left + (1000*right) + (1000000*idx)
	if v, ok := ymap[k]; ok {
		return v
	} else {
		v := 0
		for x := left; x <= right; x++ {
			v+=table[idx][x]
		}
		ymap[k] = v
		return v
	}
}

func main() {
	var n,m int
	fmt.Scanf("%d %d\n", &n, &m)
	table = make([][]int, n)
	for y := 0; y < n; y++ {
		table[y] = make([]int, m)
		for x := 0; x < m; x++ {
			fmt.Scanf("%d", &table[y][x])
		}
	}
	var count int
	for y1 := 0; y1 < n; y1++ {
		for y2 := 0; y2 < n; y2++ {
			for x1 := 0; x1 < m; x1++ {
				for x2 := 0; x2 < m; x2++ {
					sum := 0
					for y := y1; y <= y2; y++ {
						sum += getRow(y, x1, x2)
					}
					if sum % 2 == 1 {
						count++
					}
				}
			}
		}
	}
	fmt.Println(count)
}

