enum MO = 998244353L;
enum LIM = 610;

int M, N;
int[][] A;

void main() {
  auto two = new long[LIM];
  two[0] = 1;
  foreach (i; 1 .. LIM) {
    two[i] = (two[i - 1] * 2) % MO;
  }

  try {
    for (; ; ) {
      M = readInt();
      N = readInt();
      A = new int[][](M, N);
      foreach (i; 0 .. M) foreach (j; 0 .. N) {
        A[i][j] = readInt();
      }

      auto a = new int[][](M, N);
      foreach (i; 0 .. M) foreach (j; 0 .. N) {
        a[i][j] = A[i][j];
      }
      int r;
      foreach (h; 0 .. N) {
        foreach (i; r .. M) {
          if (a[i][h]) {
            swap(a[r], a[i]);
            break;
          }
        }
        if (r < M && a[r][h]) {
          foreach (i; r + 1 .. M) {
            if (a[i][h]) {
              foreach (j; h .. N) {
                a[i][j] ^= a[r][j];
              }
            }
          }
          ++r;
        }
      }
      debug {
        writeln("rank = ", r);
      }
      long ans;
      ans += two[M + N - 1];
      ans -= two[M + N - 1 - r];
      ans = (ans % MO + MO) % MO;
      writeln(ans);
    }
  } catch (EOFException e) {
  }
}

