def powe(n,e):
    ret=1
    b=n
    while e>0:
        if e%2==1:
            ret*=b
        b=b*b
        ret%=mod
        b%=mod
        e//=2
    return ret

table = [[0]*301 for x in range(301)]

def inv(q):
    return powe(q,mod-2)

for i in range(301):
    table[i][0]=1
for p in range(301):
    for q in range(1,p+1):
        table[p][q] = (table[p][q-1]*(p-q+1)*inv(q))%mod
def nck(p,q):
    return table[p][q]
