        input_raw = read_func().strip().split()
        a = 0
        for j in range(M):
            a += int(input_raw[j]) << j
        A.append(a)
    is_set = set([])
    is_all_zero_set = set([])
    A_ = [0 for i in range(N)]
    all_zero_count = 0
    for i in range(max(M, N)):
        is_all_zero = True
        for j in range(N):
            if (A[j] & (0x01 << i)) > 0:
                is_all_zero =False
