fn rank(mut a: Vec<Vec<i32>>) -> usize {

    let n = a.len();
    let m = a[0].len();
    let mut rank = 0;
    for j in 0..m {
        'lp: for i in rank..n {
            if a[i][j] == 1 {
                if i != rank {
                    for k in 0..m {
                        let tmp = a[rank][k];
                        a[rank][k] = a[i][k];
                        a[i][k] = tmp;
                    }
                }

                rank += 1;
                for d in rank..n {
                    if a[d][j] == 1 {
                        for k in 0..m {
                            a[d][k] ^= a[rank][k];
                        }
                    }
                }
                break 'lp;
            }
        }
    }
    // debug!(a);
    rank
}

