    println!("{}", ans % d);
}

fn rank2(a: &Vec<Vec<usize>>) -> usize {
    let n = a.len();
    let m = a[0].len();
    let mut b = a.clone();
    let mut r = 0;
    for i in 0..n {
        let mut idx = 0;
        while idx < m && b[i][idx] == 0 {
            idx += 1;
        }
        if idx == m {
            continue;
        }
        for j in i + 1..n {
            for k in 0..m {
                b[j][k] = (b[j][k] + b[i][k]) & 1;
            }
        }
        r += 1;
    }
    r
}

fn pow_m(n: usize, mut p: usize, d: usize) -> usize {
    let mut r = n;
    let mut ret = 1;
    while p > 0 {
        if p % 2 == 0 {
            r = r * r % d;
            p /= 2;
        } else {
            ret = ret * r % d;
            p -= 1;
        }
    }
    ret
}

