    for col in 0..m {
        match (rank..n).find(|&j| a[j][col] == 1) {
            Some(row) => {
                a.swap(rank, row);
                for i in 0..n {
                    if i == rank {
                        continue;
                    }
                    for j in 0..m {
                        a[i][j] ^= a[rank][j];
                    }
                }
                rank += 1;
            }
            None => {}
        }
    }

    let mut pow2 = vec![0; n + m + 1];
    pow2[0] = 1;
    for i in 1..pow2.len() {
        pow2[i] = (pow2[i - 1] * 2) % MOD;
    }
    let ans = pow2[n + m - 1] + MOD - pow2[n + m - rank - 1];
    println!("{}", ans % MOD);

