fn gauss_jordan(mut a: Vec<Vec<usize>>) -> usize {
    let n = a.len();
    let m = a[0].len();
    let mut rank = 0;
    let mut row = 0;
    for col in 0..m {
        let mut pivot = None;
        for i in row..n {
            if a[i][col] == 1 {
                pivot = Some(i);
                break;
            }
        }
        if pivot.is_none() {
            continue;
        }

        let pivot = pivot.unwrap();
        a.swap(row, pivot);
        rank += 1;
        for k in row + 1..n {
            if a[k][col] == 1 {
                for j in 0..m {
                    a[k][j] ^= a[row][j];
                }
            }
        }

        row += 1;
        if row >= n {
            break;
        }
    }
    rank
}

