const MOD: i64 = 998244353;

fn rank(mut a: Vec<Vec<i32>>) -> usize {

    let n = a.len();
    let m = a[0].len();
    let mut rank = 0;
    for j in 0..m {
        'lp: for i in 0..n {
            if a[i][j] == 1 {
                rank += 1;
                for d in i+1..n {
                    if a[d][j] == 1 {
                        for k in 0..m {
                            a[d][k] ^= a[i][k];
                        }
                    }
                }
                break 'lp;
            }
        }
    }
    rank
}

fn main() {
    input! {
        n: usize, m: usize,
        a: [[i32; m]; n]
    };
    let mut p2 = vec![0; n+m];
    p2[0] = 1;
    for i in 1..n+m {
        p2[i] = p2[i-1] * 2 % MOD;
    }


    let rank = rank(a);
    let mut ans = p2[m-1] * p2[n-rank] % MOD * (p2[rank] + MOD - 1) % MOD;

