	while(scanf("%d",&n),n)
	{
		const int OFFSET = 256;
		char a[OFFSET*2][OFFSET*3] = {0};
		for(i=0;i<n;++i)
			scanf("%s",&a[i+OFFSET][OFFSET]);
		max_c = c =0;
		for(j=OFFSET;j<OFFSET*2;++j)
			for(c=0,i=OFFSET;i<OFFSET*2;++i)
				(a[i][j]=='1') ? max_c=max(++c,max_c): c=0;
		for(i=OFFSET;i<OFFSET*2;++i)
			for(c=0,j=OFFSET;j<OFFSET*2;++j)
				(a[i][j]=='1') ? max_c=max(++c,max_c): c=0;
		for(i=0; i<OFFSET*2; ++i)
			for(c=0,j=0; j<OFFSET*3 && i+j<=OFFSET*2; ++j)
				(a[i+j][j]=='1') ? max_c=max(++c,max_c): c=0;
		for(j=0; j<OFFSET*3; ++j)
			for(c=0,i=0;i<OFFSET*2; ++i)
			{
				if( j-i>=0 && j-i<OFFSET*2)
					(a[i][j-i]=='1') ? max_c=max(++c,max_c): c=0;
			}
		printf("%d\n",max_c);
	}
	return 0;
