int solve(){
  for(int i = 1; i <= in; i++){
    for(int j = 1; j <= in; j++){
      if(data[i][j] == '1'){
	num[0][i][j] = num[0][i-1][j]+1;
	num[1][i][j] = num[1][i][j-1]+1;
	num[2][i][j] = num[2][i-1][j-1]+1;
	num[3][i][j] = num[3][i-1][j+1]+1;
      }
    }
  }
  int m = 0;
  for(int i = 1; i <= in; i++){
    for(int j = 1; j <= in; j++){
      m = max(max(max(m,num[0][i][j]),max(num[1][i][j], num[2][i][j])),num[3][i][j]);
    }
  }
  return m;
}

