struct UnionFind {
  vector<int> data;
  UnionFind(int size){
    rep(i in up(0,size)) data.pb(i); 
  }

  void unionSet(int x, int y) {
    x = root(x);
    y = root(y);
    if(x != y) data[y] = data[x];
  }

  bool findSet(int x, int y) {
    return root(x) == root(y);
  }

  int root(int x) {
    if(data[x] == x) return x;
    else root(data[x]);
  }

  int size() {
    return data.size();
  }
};

int ans,n;

void nnm(vector<string> vec,int x, int y, bool go_rigth,int cnt){
  debug_var(x,y,cnt);
  if(x >= n - 1 || y >= n - 1) return;

  if(vec[y][x] == '1'){
    cnt++;
    ans = max(ans, cnt);
  }else{
    cnt = 0;
  }

  if(go_rigth) x++;
  else x--;

  y++;
  if(x <= 0 || y <= 0) return;
  nnm(vec,x, y, go_rigth, cnt);
}

