			MAX = max(sum,MAX);
		}
		rep(i,n){
			sum = 0;
			rep(j,n)sum += p[i+j*n];
			MAX = max(sum,MAX);
		}
		rep(i,n){
			sum = 0;
			rep(j,n-i)sum += p[i+j*(n+1)];
			MAX = max(sum,MAX);
		}
		rep(i,n){
			sum = 0;
			rep(j,n-i)sum += p[(n-1-i)+j*(n-1)];
			MAX = max(sum,MAX);
		}

		rep(i,n){
			sum = 0;
			rep(j,i+1)if(n*n-i-j*(n+1)<n*n)sum += p[n*n-i-j*(n+1)];
			MAX = max(sum,MAX);
		}

		rep(i,n){
			sum = 0;
			rep(j,i+1)if(n*n-i-j*(n+1)<n*n)sum += p[n*(n-1)+i-j*(n-1)];
			MAX = max(sum,MAX);
		}
