#define minup(a,b) (a=min(a,b))
#define INF 999999999

int n;

int exe(int y,int x,int dy,int dx,vector<string> data){
    int tmp = 0;
    while(1){
        if( !(x>=0 && y>=0 && x<n && y<n) ){
            break;
        }
        tmp+=data[y][x]-'0';
        x+=dx,y+=dy;
    }
    return tmp;
}


int solve(int y,int x,vector<string> data){
    int lu=0,ld=0;
    int ru=0,rd=0;

    rd = exe(y,x,1,1,data);
    ru = exe(y,x,-1,1,data);
    ld = exe(y,x,1,-1,data);
    lu = exe(y,x,-1,-1,data);

    return max(rd+lu,ru+ld)-(data[y][x]-'0');
}


