				J = j;
				while(G[I-1][J+1]=='1'){
					cnt++;
					I--;
					J++;
				}
				I = i;
				J = j;
				while(G[I+1][J-1]=='1'){
					cnt++;
					I++;
					J--;
				}
				if(max<cnt)
					max = cnt;
			}
		}

		cout << max << endl;
	}

	return 0;
}

