class Cell{
public:
  int d, r, rd, ld;
  Cell(int d = 0, int r = 0, int rd = 0, int ld = 0)
    : d(d), r(r), rd(rd), ld(ld) {}
  int max() {
    if(d >= r && d >= rd && d >= ld) return d;
    if(r >= d && r >= rd && r >= ld) return r;
    if(rd >= d && rd >= r && rd >= ld) return rd;
    if(ld >= d && ld >= r && ld >= rd) return ld;
  }
};

