	  c[i][j].d = c[i-1][j].d + 1;
	  c[i][j].r = c[i][j-1].r + 1;
	  c[i][j].rd = c[i-1][j-1].rd + 1;
	  c[i][j].ld = c[i-1][j+1].ld + 1;
