	int res = 0;
	for(int x=0;x<n;x++){
		int c=0;
		for(int y=0;y<n;y++){
			if(mat[y][x] == 0) { res = max(res,c); c = 0; }
			else c++;
		}

		res = max(c,res);
	}

	return res;
}

int hori(int n,int** mat){
	int res = 0;
	for(int y=0;y<n;y++){
		int c=0;
		for(int x=0;x<n;x++){
			if(mat[y][x] == 0) { res = max(res,c); c = 0; }
