					tmp = 0; loop(v, i, j, HIDARI, n, 0);
					h = tmp;

					tmp = 0; loop(v, i, j, N_U_MIGI, n, 0);
					num = tmp;

					tmp = 0; loop(v, i, j, N_S_MIGI, n, 0);
					nsm = tmp;

					tmp = 0; loop(v, i, j, N_U_HIDARI, n, 0);
