					//cout <<"m + h = " <<m + h + 1 <<endl;
					//cout <<"num + nsh = " <<num + nsh + 1 <<endl;
					//cout <<"nuh + nsm = " <<nuh + nsm + 1 <<endl;

					if(max < u + s + 1){
						max = u + s + 1;
					} if(max < m + h + 1){
						max = m + h + 1;
					} if(max < num + nsh + 1){
						max = num + nsh + 1;
					} if(max < nuh + nsm + 1){
						max = nuh + nsm + 1;
					}
					//cout <<"max = " <<max <<endl;

				}
			}
		}
		cout <<max <<endl;
	}
	return 0;
}

void loop(string v[], int y, int x, int type, int n, int deep){
	int plusx[8] = {0, 0, 1, -1, 1, 1, -1, -1}, plusy[8] = {-1, 1, 0, 0, -1, 1, -1, 1};
	if(x < 0 || y < 0 || x >= n || y >= n || v[y][x] == '0'){
		//cout <<"x = " <<x <<" y = " <<y <<" deep - 1 = " <<deep - 1 <<endl;
		tmp += (deep - 1);
	} else{
		loop(v, (y + plusy[type]), (x + plusx[type]), type, n, deep + 1);
	}
}

