int line(vector<string> v, int len)
{
	int n = v.size();
	int x = len - 1, y = 0;
	int res = 0;

	// ツ右ツ湘」ツつゥツづァツ湘」ツ板シツ閉ェ
	string s;
	rep(i, len)
	{
		s.push_back(v[y][x]);
		x--;
		y++;
	}

	chmax(res, count(s));
	s.clear();

	// ツ右ツ湘」ツつゥツづァツ可コツ板シツ閉ェ
	x = n - 1;
	y = n - len;
	rep(i, len)
	{
		s.push_back(v[y][x]);
		x--;
		y++;
	}

	chmax(res, count(s));
	s.clear();

	// ツ債カツ湘」ツつゥツづァツ湘」ツ板シツ閉ェ
	x = n - len;
	y = 0;
	rep(i, len)
	{
		s.push_back(v[y][x]);
		x++;
		y++;
	}

	chmax(res, count(s));
	s.clear();

	// ツ債カツ湘」ツつゥツづァツ可コツ板シツ閉ェ
	x = 0;
	y = n - len;
	rep(i, len)
	{
		s.push_back(v[y][x]);
		x++;
		y++;
	}

	chmax(res, count(s));

	return res;	
}
int main()
{
    int n;
	while(cin >> n && n)
	{
		int res = 0;
		vector<string> v(n);
		rep(i, n) cin >> v[i];


		rep(i, n)
		{
			chmax(res, count(v[i]));

			string s;
			rep(j, n) s.push_back(v[j][i]);

			chmax(res, count(s));
		}

		for(int i = n; 0 < i; i--)
			chmax(res, line(v, i));

		cout << res << endl;
	}
}

