            max = getMax( max, getDiagnal2( n, m, 0, i ) );
        }
        for( int i = 1; i < n; i++ )
            max = getMax( max, getDiagnal1( n, m, i, n - 1 ) );
        for( int i = 1; i < n; i++ )
            max = getMax( max, getDiagnal2( n, m, i, 0 ) );

        cout << max << endl;
    }

