int getMax( int a, int b )
{
    if( a > b )
        return a;
    else
        return b;
}

int getWidth( int n, char m[255][255], int s, int t )
{
    int cnt = 0, max = 0;
    while( t < n )
    {
        if( m[s][t] == '1' )
        {
            cnt++;
            if( cnt > max )
                max = cnt;
        }
        else
            cnt = 0;
        t++;
    }
    return max;
}

