{
    int cnt = 0, max = 0;
    while( s < n )
    {
        if( m[s][t] == '1' )
        {
            cnt++;
            if( cnt > max )
                max = cnt;
        }
        else
            cnt = 0;
        s++;
    }
    return max;
}

int getDiagnal1( int n, char m[255][255], int s, int t )
{
    int cnt = 0, max = 0;
    while( s < n && t >= 0 )
    {
        if( m[s][t] == '1' )
        {
            cnt++;
            if( cnt > max )
                max = cnt;
        }
        else
            cnt = 0;
        s++;
        t--;
    }
    return max;
}


int getDiagnal2( int n, char m[255][255], int s, int t )
{
    int cnt = 0, max = 0;
