			break;
		} // end if

		vector <vector <int> > grid (n, vector <int> (n, 0 ) );

		int i,j,k;

		for (i = 0; i < n; ++i ){
			getline (cin, str );
			for (j = 0; j < n; ++j ){
				grid[i][j] = str[j] - '0';
